import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle, IoMdCart } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Products({ products }) {

    const dispacth = useDispatch();
    //const productsState = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {
        action === 'add' ? setCount(count + 1) : setCount(count > 0 ? count - 1 : 0);
    }

    function addCart(prod, action, index) {
        console.log('ddd', count)
        if (action === 'add' && count > 0) {
            setCount(0);
            dispacth(addShoppingProduct(prod, index));

        } else if (action === 'sub') {
            dispacth(subShoppingProduct(products, index));
        }
    }

