const INITIAL_STATE = []

export default function AddShoppingCarReducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'ADD_SHOPPING_CAR':
            return [...state,  action.product]
        default:
            return state;
    }
}

export const addShoppingCar = product => {
    return {
        type: 'ADD_SHOPPING_CAR',
        product
    }
}
