import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingCar, subShoppingCar } from '../../store/addReducer'

import { FaBeer } from 'react-icons/io';

import './styles.css';

export default function Products({ products }) {

	const dispacth = useDispatch();
	const cars = useSelector(state => state);

	const [count, setCount] = useState(0);

	function handlerShoppingCar(products, action) {

		if (action === 'add') {
			setCount(count + 1);
			//dispacth(addShoppingCar(car));

		} else {
			setCount(count > 0 ? count - 1 : 0);
			//dispacth(subShoppingCar(car));
		}

		
	}
	return (
		<div className="col-sm-3 mt-3 mb-3">
			<div className="card">
				<img src={products.url} className="card-img-top" alt={products.name} />
				<div className="card-body">
					<h5 className="card-title">{products.name}</h5>
					<h5 className="card-title">{products.unidade}</h5>
					<h5 className="card-title">{products.valor}</h5>
					<ul className="navbar-nav ml-md-auto">
						<div to="/cart" activeClassName="active" className="nav-item">
							<input type="text" name="name" value={count} />
						</div>
					</ul>
					<button onClick={() => handlerShoppingCar(products, 'add')} className="btn btn-primary">
						<i className="fa fa-cart-plus fa-2x" aria-hidden="true" />
					</button>
					<IoIosAddCircle onClick={() => handlerShoppingCar(products, 'sub')} className="btn btn-primary">
						<i className="fa fa-cart-plus fa-2x" aria-hidden="true" />
					</IoIosAddCircle>
				</div>
			</div>
		</div>
	);
}
