const INITIAL_STATE = []

export default function AddShoppingProductsReducer(state = INITIAL_STATE, action, index) {
    switch (action.type) {
        case 'ADD_SHOPPING_PRODUCT':
            return [...state, action.product]
        case 'REMOVE_SHOPPING_PRODUCT':
            console.log('eee', index)
            return [...state, action.product]
        default:
            return state;
    }
}

export const addShoppingProduct = product => {
    return {
        type: 'ADD_SHOPPING_PRODUCT',
        product,
        index
    }
}

export const subShoppingProduct = product => {
    return {
        type: 'REMOVE_SHOPPING_PRODUCT',
        product,
        index
    }
}
