import React, { useState, useEffect } from "react";

import { useDispatch } from "react-redux";

//import { addCar } from '../../store/reducer'


export default function Add() {

	const [form, setForm] = useState({});

	//const [form, setForm] = useState({ name: '', url: '', id: Math.floor(Math.random() * 65536) });

	//const dispacth = useDispatch();

	function formChange(e) {
		setForm({ ...form, [e.target.name]: e.target.value });
	}

	function onSubmit(e) {
		e.preventDefault();
		//dispacth(addCar(form));
		//postItem(form);

		console.log('fdd', form)

	}

	async function postItem() {
		try {
			await fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`, {
				method: 'POST',
				body: form,
			}
			).then(
				response => response.json()
			)
		} catch (error) {
			alert('erro, verifique a conexão com a internet');
		}
	}

	return (
		<form className="container mt-5" onSubmit={onSubmit}>
			<div className="form-group">
				<label>Nome</label>
				<input onChange={formChange} type="text" name="name" value={form.name} className="form-control" placeholder="Nome..." />
			</div>
			<div className="form-group">
				<label>unidade:</label>
				<input onChange={formChange} type="text" name="unidade" value={form.unid} className="form-control" placeholder="Unidade..." />
			</div>
			<div className="form-group">
				<label>valor</label>
				<input onChange={formChange} type="text" name="valor" value={form.name} className="form-control" placeholder="valor..." />
			</div>
			<div className="form-group">
				<label>imagem:</label>
				<input onChange={formChange} type="text" name="image" value={form.url} className="form-control" placeholder="URL:https://produto" />
			</div>
			<button type="submit" className="btn btn-primary">
				Adicionar
			</button>
		</form>
	);
}
