import React, { useEffect, useState } from 'react';
//import { useSelector } from 'react-redux';
import Products from '../../Components/Produts';
import ReactLoading from 'react-loading';

export default function List() {
	//const products = useSelector(state => state);


	const [products, setproducts] = useState({});

	const [loading, setLoading] = useState(true);

	useEffect(() => {
		try {
			fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`
			).then(
				response => response.json()
			).then(
				data => (setproducts(data), setLoading(true)),
			)
		} catch (error) {
			alert('erro, verifique a conexão com a internet');
		}
	}, [])

	return (
		<>
			{loading ?
				<div className="loading">
					<ReactLoading type={'spinningBubbles'} color={'#ccc'} height={167} width={175} />
				</div>
				
				: (
					<>
						<div className="container-fluid">
							<div className="row">{products.length > 0 ?  products.map(product => <Products products={product.form} />) : <h1>Loja vazia</h1>}</div>
						</div>
					</>
				)
			}
		</>
	);

}
