import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import Products from '../../Components/Produts';

export default function List() {
	const productsState = useSelector(state => state);


	const [products , setproducts] = useState({});

	const [loading , setLoading] = useState(false);

	useEffect(() => {
		
        try {
            fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`
            ).then(
                response => response.json()
            ).then(
                data => (setproducts(data), setLoading(false)),
            )
        } catch (error) {
            alert('erro, verifique a conexão com a internet');
        }
	})

	return (
		<div className="container-fluid">
		{console.log('products', products)}
			<div className="row">{products.map((product, index) => <Products key={index} products={product} />)}</div>
		</div>
	);
}
