import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle, IoMdCart } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Products({ products }) {

    const dispacth = useDispatch();
    //const productsState = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {
        action === 'add' ? setCount(count + 1) : setCount(count > 0 ? count - 1 : 0);
    }

    function addCart(prod, action, index) {
        console.log('ddd', count)
        if (action === 'add' && count > 0) {
            setCount(0);
            dispacth(addShoppingProduct(prod, index));

        } else if (action === 'sub') {
            dispacth(subShoppingProduct(products, index));
        }
    }

    return (
        <section className="col-sm-3 mt-3 mb-3">
            <div className="card">
                <img style={{ backgroundColor: '#b9b6b6'}} src={products.image} className="card-img-top" alt={products.name} />
                <div className="card-body" style={{ backgroundColor: '#ccc'}}>
                    <h5 className="name"><strong>{products.name}</strong></h5>
                    <h5 className="unid">{products.unidade}</h5>
                    <h5 className="product-value">{products.valor}</h5>
                    <div className="teste" >
                        <div style={{textAlign: 'center'}}>
                            <MdRemoveCircle style={{margin: 10}} fontSize={25} onClick={() => handlerShoppingCar(products, 'sub')} />
                            <input style={{ width: '30%', margin: 10, backgroundColor: "#FFF",  borderRadius: 10,marginBottom: 8}}type="text" name="name" value={count} />
                            <IoIosAddCircle style={{margin: 10}} fontSize={25} onClick={() => handlerShoppingCar(products, 'add')} />
                            <IoMdCart  style={{margin: 10, fontSize: 30}} fontSize={30} onClick={() => addCart(products, 'add')} className="card-title">adicionar ao carrinho</IoMdCart>
                        </div>

                    </div>
                </div>
            </div>
        </section>

    );
}
