import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle, IoMdCart } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Products({ products }) {

    const dispacth = useDispatch();
    //const productsState = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {
        action === 'add' ? setCount(count + 1) : setCount(count > 0 ? count - 1 : 0);
    }

    function addCart(prod, action, index) {
        console.log('ddd', count)
        if (action === 'add' && count > 0) {
            setCount(0);
            dispacth(addShoppingProduct(prod, index));

        } else if (action === 'sub') {
            dispacth(subShoppingProduct(products, index));
        }
    }

    {/* <div className="containerItem" >
                        <div style={{textAlign: 'center'}}>
                            <MdRemoveCircle fontSize={25} onClick={() => handlerShoppingCar(products, 'sub')} />
                            <input style={{ width: '25%', backgroundColor: "#FFF",  borderRadius: 10,marginBottom: 8}}type="text" name="name" value={count} />
                            <IoIosAddCircle fontSize={25} onClick={() => handlerShoppingCar(products, 'add')} />
                            <button style={{margin: 10, fontSize: 10}} onClick={() => addCart(products, 'add')} className="card-title">adicionar ao carrinho</button>
                            </div>

                        </div> */}

    return (
        <section className="col-sm-4 mt-4 mb-4">
            <div className="card">
                <img style={{ backgroundColor: '#b9b6b6', borderBottom: 'solid' }} src={products.url} className="card-img-top" alt={products.name} />
                <div className="card-body" style={{ backgroundColor: '#fff' }}>
                    <h1 className="name"><strong>{products.name}</strong></h1>
                    <h2 className="unid">{products.unidade}</h2>
                    <h3 className="product-value">DR${products.valor}/unid</h3>
                    <section class="containerTeste flex1">
                        <MdRemoveCircle className="item" fontSize={25} onClick={() => handlerShoppingCar(products, 'sub')} />    
                        <input className="item"  style={{ width: '25%', backgroundColor: "#FFF",  borderRadius: 10,marginBottom: 8}}type="text" name="name" value={count} />
                        <IoIosAddCircle className="item"  fontSize={25} onClick={() => handlerShoppingCar(products, 'add')} />
                        <button  className="item"  style={{margin: 10, fontSize: 10}} onClick={() => addCart(products, 'add')}>adicionar ao carrinho</button>
                    </section>
                </div>
            </div>
        </section>

    );
}
