import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Products({ products }) {

    const dispacth = useDispatch();
    const cars = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {

        if (action === 'add') {
            setCount(count + 1);
            dispacth(addShoppingProduct(products, index));

        } else {
            setCount(count > 0 ? count - 1 : 0);
            dispacth(subShoppingProduct(products, index));
        }


    }
    return (
        <div className="col-sm-3 mt-3 mb-3">
            <div className="card">
                <img src={products.url} className="card-img-top" alt={products.name} />
                <div className="card-body">
                    <h5 className="card-title">{products.name}</h5>
                    <h5 className="card-title">{products.unidade}</h5>
                    <h5 className="card-title">{products.valor}</h5>
                    <div className="container-fluid">
                        <div className="row">
                                <MdRemoveCircle fontSize={30} className="col-sm-4 mt-4 mb-4" onClick={() => handlerShoppingCar(products, 'sub', key)} />
                                 <input className="col-sm-4 mt-4 mb-4" type="text" name="name" value={count} />
                                <IoIosAddCircle  fontSize={30}  className="col-sm-4 mt-4 mb-4" onClick={() => handlerShoppingCar(products, 'add', key)} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
