// export default Index;

import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
// import { addShoppingCar, subShoppingCar } from '../../store/addReducer'

import './styles.css';

export default function Car({ car }) {

	console.log('dddd', car)
	// const dispacth = useDispatch();
	const cars = useSelector(state => state);

	console.log('dddd', cars.data.url)
	const [count, setCount] = useState(0);

	// function count() {

	// }
	function handlerShoppingCar(car, action) {

		if (action === 'add') {
			setCount(count + 1);
			//dispacth(addShoppingCar(car));

		} else {
			setCount(count > 0 ? count - 1 : 0);
			//dispacth(subShoppingCar(car));
		}


	}
	return (

		<div className="container-fluid">
			<div className="row">
				<div className="col-sm-3 mt-3 mb-3">
					<div className="card">
						<img src={car.url} className="card-img-top" alt={car.name} />
						<div className="card-body">
							<h5 className="card-title">{car.name}</h5>
							<h5 className="card-title">{car.unidade}</h5>
							<h5 className="card-title">{car.valor}</h5>
							<ul className="navbar-nav ml-md-auto">
								<div to="/cart" activeClassName="active" className="nav-item">
									<input type="text" name="name" value={count} />
									<span className="fa-counter">{count}</span>
								</div>
							</ul>
							<button onClick={() => handlerShoppingCar(car, 'add')} className="btn btn-primary">
								<i className="fa fa-cart-plus fa-2x" aria-hidden="true" />
							</button>
							<button onClick={() => handlerShoppingCar(car, 'sub')} className="btn btn-primary">
								<i className="fa fa-cart-plus fa-2x" aria-hidden="true" />
							</button>
						</div>
					</div>
				</div>
			</div>
		</div>



	);
}
