import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import Products from '../../Components/Produts';

export default function List() {
	const products = useSelector(state => state);


	//const [products, setproducts] = useState({});

	const [loading, setLoading] = useState(false);

	// useEffect(() => {
    //    console.log('fff')
	// 	try {
	// 		fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`
	// 		).then(
	// 			response => response.json()
	// 		).then(
	// 			data => (setproducts(data), setLoading(false)),
	// 		)
	// 	} catch (error) {
	// 		alert('erro, verifique a conexão com a internet');
	// 	}
	// })

	return (
		<>
			{loading ?
				<div style={{ display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
					<div>loading</div>
				</div>
				: (
					<>
						<div className="container-fluid">
							<div className="row">{products.data.map(product => <Products products={product} />)}</div>
						</div>
					</>
				)
			}
		</>
	);

}
