import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle, IoMdCart } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Products({ products }) {

    const dispacth = useDispatch();
    //const productsState = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {
        action === 'add' ? setCount(count + 1) : setCount(count > 0 ? count - 1 : 0);
    }

    function addCart(prod, action, index) {

        console.log('fff', prod, action, index)
        if (action === 'add') {
            setCount(0);
            // const teste = products.filter(x => x.id === prod.id);

            // console.log('rrr', teste);
            dispacth(addShoppingProduct(prod, index));

        } else {
            dispacth(subShoppingProduct(products, index));
        }
    }

    return (
        // <div className="col-sm-3 mt-3 mb-3">
        //     <div className="card">
        //         <img src={products.url} className="card-img-top" alt={products.name} />
        //         <div className="card-body">
        //             <h5 className="card-title">{products.name}</h5>
        //             <h5 className="card-title">{products.unidade}</h5>
        //             <h5 className="card-title">{products.valor}</h5>
        //             <div className="container-fluid">
        //                 <div className="row">
        //                         <MdRemoveCircle fontSize={30} className="col-sm-4 mt-4 mb-4" onClick={() => handlerShoppingCar(products, 'sub' )} />
        //                          <input className="col-sm-4 mt-4 mb-4" type="text" name="name" value={count} />
        //                         <IoIosAddCircle  fontSize={30}  className="col-sm-4 mt-4 mb-4" onClick={() => handlerShoppingCar(products, 'add')} />
        //                 </div>
        //             </div>
        //             <h5 onClick={() => addCart(products, 'add')} className="card-title">adicionar ao carrinho</h5>
        //         </div>
        //     </div>

        // </div>

        <section className="col-sm-3 mt-3 mb-3">
            <div className="card">
                <img src={products.url} className="card-img-top" alt={products.name} />
                <div className="card-body">
                    <h5 className="name"><strong>{products.name}</strong></h5>
                    <h5 className="unid">{products.unidade}</h5>
                    <h5 className="product-value">{products.valor}</h5>
                    <div className="col-sm-3 mt-3 mb-3 teste">
                        <div>
                            <MdRemoveCircle fontSize={30} onClick={() => handlerShoppingCar(products, 'sub')} />
                            <input type="text" name="name" value={count} />
                            <IoIosAddCircle fontSize={30} onClick={() => handlerShoppingCar(products, 'add')} />
                        </div>

                    </div>
                    <IoMdCart onClick={() => addCart(products, 'add')} className="card-title">adicionar ao carrinho</IoMdCart>
                </div>
            </div>
        </section>

    );
}
