import React, { useState, useEffect } from "react";

import { useDispatch } from "react-redux";

//import { addCar } from '../../store/reducer'


export default function Add() {

	const [form, setForm] = useState({});

	//const [form, setForm] = useState({ name: '', url: '', id: Math.floor(Math.random() * 65536) });

	//const dispacth = useDispatch();

	function formChange(e) {
		setForm({ ...form, [e.target.name]: e.target.value });
	}

	function onSubmit(e) {
		e.preventDefault();
		//dispacth(addCar(form));
		postItem(form);


	}

	async function postItem() {
		axios.post(`https://jsonplaceholder.typicode.com/users`, { form })
		.then(res => {
			console.log(res);
			console.log(res.data);
		})
		console.log('fff', form);
		const teste = await fetch('https://jsonbox.io/box_f486e56d8b1637c82740/products', {
			method: 'POST',
			headers: {'Content-Type':'application/json'},
			body: 
			[
				{
					name: "Modelo Negra",
					unidade: "12, Unit-33oz Bottle",
					valor: "32",
					url:
						"https://qa-m1-dr.abi-sandbox.net/media/catalog/product//-/R/-R002151.png"
				}
			]

		}).then(res => {
			console.log('fefe', res)
		});

		// try {
		// 	await fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`, {
		// 		form,
		// 	}
		// 	).then(
		// 		response => response.json()
		// 	)
		// } catch (error) {
		// 	alert('erro, verifique a conexão com a internet');
		// }
	}

	return (
		<form className="container mt-5" onSubmit={onSubmit}>
			<div className="form-group">
				<label>Nome</label>
				<input onChange={formChange} type="text" name="name" value={form.name} className="form-control" placeholder="Nome..." />
			</div>
			<div className="form-group">
				<label>unidade:</label>
				<input onChange={formChange} type="text" name="unidade" value={form.unity} className="form-control" placeholder="Unidade..." />
			</div>
			<div className="form-group">
				<label>valor</label>
				<input onChange={formChange} type="text" name="valor" value={form.value} className="form-control" placeholder="valor..." />
			</div>
			<div className="form-group">
				<label>imagem:</label>
				<input onChange={formChange} type="text" name="image" value={form.image} className="form-control" placeholder="URL:https://produto" />
			</div>
			<button type="submit" className="btn btn-primary">
				Adicionar
			</button>
		</form>
	);
}
