import React, { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addShoppingProduct, subShoppingProduct } from '../../store/addReducer'


import { IoIosAddCircle, IoMdCart } from 'react-icons/io';
import { MdRemoveCircle } from 'react-icons/md';
import './styles.css';

export default function Home({  }) {

    const [products, setproducts] = useState({});

    const [loading, setLoading] = useState(true);

    useEffect(() => {
        try {
            fetch(`https://jsonbox.io/box_f486e56d8b1637c82740/products`
            ).then(
                response => response.json()
            ).then(
                data => (setproducts(data), setLoading(false)),
            )
        } catch (error) {
            alert('erro, verifique a conexão com a internet');
        }
    }, [])




    const dispacth = useDispatch();
    //const productsState = useSelector(state => state);

    const [count, setCount] = useState(0);

    function handlerShoppingCar(products, action, index) {
        action === 'add' ? setCount(count + 1) : setCount(count > 0 ? count - 1 : 0);
    }

    function addCart(prod, action, index) {

        console.log('fff', prod, action, index)
        if (action === 'add') {
            setCount(0);
            // const teste = products.filter(x => x.id === prod.id);

            // console.log('rrr', teste);
            dispacth(addShoppingProduct(prod, index));

        } else {
            dispacth(subShoppingProduct(products, index));
        }
    }

    return (
        <>
            {console.log('dd', products)}
            {loading ?
                <div style={{ display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
                    <div>loading</div>
                </div>
                : (
                    <>
                        <div className="container-fluid">
                            <div className="row">
                                {products.length > 0 ? products.map(product =>
                                    <div className="col-sm-3 mt-3 mb-3">
                                        <div className="card">
                                            <img src={product.url} className="card-img-top" alt={product.name} />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.name}</h5>
                                                <h5 className="card-title">{product.unidade}</h5>
                                                <h5 className="card-title">{product.valor}</h5>
                                                <div className="container-fluid">
                                                    <div className="row">
                                                        <MdRemoveCircle fontSize={30} className="col-sm-4 mt-4 mb-4" onClick={() => handlerShoppingCar(product, 'sub')} />
                                                        <input className="col-sm-4 mt-4 mb-4" type="text" name="name" value={count} />
                                                        <IoIosAddCircle fontSize={30} className="col-sm-4 mt-4 mb-4" onClick={(e) => handlerShoppingCar(product, 'add', e)} />
                                                    </div>
                                                </div>
                                                <h5 onClick={() => addCart(product, 'add')} className="card-title">adicionar ao carrinho</h5>
                                            </div>
                                        </div>
                                    </div>
                                ) :
                                    <h1>
                                        Loja vazia
                                    </h1>
                                }</div>
                        </div>
                    </>
                )
            }
        </>
    );
}
